mapper.select:
SELECT * FROM bi_page


        AND PAGE_NAME = #{biPage.page_Name}


        AND PAGE_TYPE = #{biPage.page_Type}


        AND PAGE_TITLE = #{biPage.page_Title}


        AND PAGE_URL = #{biPage.page_Url}


        AND MENU_ID = #{biPage.menu_Id}


        AND MENU_NAME = #{biPage.menu_Name}

mapper.delete:
DELETE  FROM bi_block WHERE bi_page_id= #{bi_Page_Id};

DELETE FROM bi_report_field  WHERE REPORT_ID IN (
    select br.REPORT_ID FROm bi_report br where br.PAGE_ID=(
        select bp.BI_PAGE_ID FROM bi_page bp where bp.BI_PAGE_ID=#{bi_Page_Id}
    )
);


DELETE FROM bi_report  WHERE PAGE_ID = #{bi_Page_Id};

DELETE FROM bi_screen  WHERE PAGE_ID = #{bi_Page_Id};

DELETE FROM bi_page WHERE BI_PAGE_ID = #{bi_Page_Id};
